UPDATE locales_page_Text SET text_loc8='All day long he nibbled on flowers.  "Flowers taste good!" Fluffy would tell his friend.  $B' WHERE entry=3421; /* text: All day long he nibbled on flowers.  "Flowers taste good!" Fluffy would tell his friend.  $B */
