UPDATE `locales_quest` SET `offerrewardtext_loc8`='If you must take these hides to Thunder Bluff, then you are speaking to the right orc!' WHERE `entry`=6361;
UPDATE `locales_quest` SET `offerrewardtext_loc8`='Ah, a new bundle of hides. I will get to work on these immediately!$B$BThank you, brother. You have done me a great service. Here are some coins to pay for your time and travel costs.' WHERE `entry`=6362;
