UPDATE `locales_quest` SET `offerrewardtext_loc8`='The Burning Legion? Allied with Kael\'thas Sunstrider?$B$BThis is dire news. It explains much about what we\'ve seen here and in Shadowmoon.$B$BWe must send word back to Shattrath City immediately.' WHERE `entry`=10323;
UPDATE `locales_quest` SET `offerrewardtext_loc8`='You continue to prove your worth, $N. Fight the enemies of the Light wherever you may find them; your actions will be well-rewarded.' WHERE `entry`=10326;
UPDATE `locales_quest` SET `offerrewardtext_loc8`='You continue to prove your worth, $N. Fight the enemies of the Light wherever you may find them; your actions will be well-rewarded.' WHERE `entry`=10327;
UPDATE `locales_quest` SET `offerrewardtext_loc8`='Excellent job, $N! Let us see what can be learned from these briefings.' WHERE `entry`=10328;
UPDATE `locales_quest` SET `offerrewardtext_loc8`='Of course it worked! I\'m a spymaster; my information is always accurate.$B$BAnyhow, I have a whole new exciting job lined up for you. You want to hear about it? Of course you do.' WHERE `entry`=10329;
UPDATE `locales_quest` SET `RequestItemsText_loc8`='Did the plan work? I was half-expecting you to get blown up, to tell you the truth.' WHERE `entry`=10329;
