UPDATE `locales_quest` SET `objectives_loc8`='Bring the Primed Key Mold to Rohok in Thrallmar.',`offerrewardtext_loc8`='Nazgrel wants me to work on a key? Does he forget we\'re in a battlefield? There are blades and spears to forge!' WHERE `entry`=10756;
