UPDATE `locales_quest` SET `offerrewardtext_loc8`='This Crimson Sigil will be investigated. They pose a threat to all life on Draenor. I foresee a day when their dark master has to answer for his crimes. Perhaps it will be you who brings him to justice, hero.' WHERE `entry`=10781;
