UPDATE `locales_quest` SET `RequestItemsText_loc8`='Did you find our missing people?' WHERE `entry`=10873;
UPDATE `locales_quest` SET `objectives_loc8`='Kirrik at the Refugee Caravan wants you to extinguish the four signal fires of Veil Shalas.',`offerrewardtext_loc8`='We witnessed the cursed smoke rise from here. You are most brave. $C.' WHERE `entry`=10874;
UPDATE `locales_quest` SET `RequestItemsText_loc8`='Veil Shalas must be silenced!' WHERE `entry`=10874;
UPDATE `locales_quest` SET `objectives_loc8`='Speak with Nazgrel in Thrallmar.' WHERE `entry`=10875;
UPDATE `locales_quest` SET `objectives_loc8`='Mekeda at the Refugee Caravan in Terokkar Forest wants you to kill 8 Cabal Skirmishers, 4 Cabal Spell-Weavers and 2 Cabal Initiates.' WHERE `entry`=10878;
